// src/App.tsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

interface JobData {
    id: number; 
    title: string;
    description: string;
    requirements: string;
    specifications: string;
}

const JobScraper: React.FC = () => {
    const [jobs, setJobs] = useState<JobData[]>([]);
    const [searchTerm, setSearchTerm] = useState<string>('');
    const [currentPage, setCurrentPage] = useState<number>(1);
    const [totalPages, setTotalPages] = useState<number>(1);
    const [isLoading, setIsLoading] = useState<boolean>(false);
    const [isScraping, setIsScraping] = useState<boolean>(false);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await axios.get<{ results: JobData[], count: number }>(
                    `http://127.0.0.1:8000/api/jobs/?page=${currentPage}&search=${searchTerm}`
                );
                setJobs(response.data.results);
                setTotalPages(Math.ceil(response.data.count / 10)); // Assuming a page size of 10
            } catch (error) {
                console.error('Error fetching jobs:', error);
            }
        };

        fetchJobs();
    }, [currentPage, searchTerm]);

    const handleSearch = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearchTerm(event.target.value);
        setCurrentPage(1);
    };

    const handlePageChange = (page: number) => {
        setCurrentPage(page);
    };

    const handleScrapeJobs = async () => {
        try {
            await axios.post('http://127.0.0.1:8000/api/run-scraper/');
            setError('Eroo')
        } catch (error) {
            console.error('Error initiating job scraping:', error);
            alert('Failed to initiate job scraping.');
        }
    };

    return (
        <div className="top">
            <h1>Job Listings</h1>
            <div className="input">
                <input
                    type="text"
                    placeholder="Search jobs..."
                    value={searchTerm}
                    onChange={handleSearch}
                />
            </div>
            <button onClick={handleScrapeJobs}>Scrape Jobs</button>
            <h1>All Available Jobs</h1>
            <div className="job-cards">
                {jobs.map(job => (
                    <div key={job.id} className="job-card">
                        <h2>{job.title}</h2>
                        <p>{job.description}</p>
                        <p>{job.requirements}</p>
                        <p>{job.specifications}</p>
                        <button>Apply</button>
                    </div>
                ))}
            </div>
            <div className="pagination">
                {Array.from({ length: totalPages }, (_, index) => (
                    <button
                        key={index}
                        onClick={() => handlePageChange(index + 1)}
                        className={currentPage === index + 1 ? 'active' : ''}
                    >
                        {index + 1}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default JobScraper;
